import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Polyline, useMapEvents } from 'react-leaflet';
import axios from 'axios';
import polyline from 'polyline';
import L from 'leaflet';

const center = [31.5, 34.8];

const ClickHandler = ({ setLanding }) => {
  useMapEvents({
    click(e) {
      setLanding(e.latlng);
    },
  });
  return null;
};

const alienIcon = (number) => L.divIcon({
  html: `
    <div style="display: flex; flex-direction: column; align-items: center; font-size: 24px;">
      <span style="font-size: 16px; font-weight: bold; color: black;">${number}</span>
      <span></span>
    </div>
  `,
  className: 'alien-icon',
  iconSize: [30, 42],
  iconAnchor: [15, 42],
});

const landingIcon = L.divIcon({
  html: '<div style="font-size: 28px;"></div>',
  className: 'landing-icon',
  iconSize: [30, 30],
  iconAnchor: [15, 30],
});

export default function App() {
  const [landing, setLanding] = useState(null);
  const [aliens, setAliens] = useState([]);

  const getRoute = async (from, to) => {
    const res = await axios.get(
      `http://localhost:5000/api/route?fromLat=${from[0]}&fromLng=${from[1]}&toLat=${to[0]}&toLng=${to[1]}`
    );
    return polyline
      .decode(res.data.routes[0].geometry)
      .map(coord => [coord[0], coord[1]]);
  };

  useEffect(() => {
    if (!landing) return;

    const createAliens = async () => {
      const directions = [0, 45, 90, 135, 180, 225, 270, 315];
      const alienPromises = directions.map(async (angle) => {
        const rad = angle * (Math.PI / 180);
        const target = [
          landing.lat + 0.05 * Math.cos(rad),
          landing.lng + 0.05 * Math.sin(rad),
        ];
        const route = await getRoute([landing.lat, landing.lng], target);
        return {
          route,
          positionIdx: 0,
          currentPos: route[0]
        };
      });

      const aliensCreated = await Promise.all(alienPromises);
      setAliens(aliensCreated);
    };

    createAliens();
  }, [landing]);

  useEffect(() => {
    const interval = setInterval(() => {
      setAliens(prevAliens => {
        return prevAliens.map(alien => {
          const { route, positionIdx } = alien;
          const nextIdx = positionIdx + 1;

          if (nextIdx >= route.length) {
            return { ...alien }; //  转砖转 住
          }

          const current = route[positionIdx];
          const next = route[nextIdx];

          const interpolated = [
            current[0] + (next[0] - current[0]) * 0.1,
            current[1] + (next[1] - current[1]) * 0.1
          ];

          return {
            ...alien,
            positionIdx,
            currentPos: interpolated
          };
        });
      });
    }, 100);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      aliens.forEach(async (alien, idx) => {
        const nextIdx = alien.positionIdx + 1;

        if (nextIdx >= alien.route.length) {
          const currentPos = alien.route[alien.route.length - 1];

          let newRoute = await getRoute(currentPos, [
            currentPos[0] + (Math.random() - 0.5) / 10,
            currentPos[1] + (Math.random() - 0.5) / 10,
          ]);

          if (!newRoute || newRoute.length < 2) {
            newRoute = alien.route.slice().reverse();
          }

          setAliens(prevAliens => {
            const updatedAliens = [...prevAliens];
            updatedAliens[idx] = {
              route: newRoute,
              positionIdx: 0,
              currentPos: newRoute[0]
            };
            return updatedAliens;
          });
        } else {
          setAliens(prevAliens => {
            const updatedAliens = [...prevAliens];
            updatedAliens[idx] = {
              ...alien,
              positionIdx: nextIdx
            };
            return updatedAliens;
          });
        }
      });
    }, 1000);

    return () => clearInterval(interval);
  }, [aliens]);

  useEffect(() => {
    const interval = setInterval(() => {
      if (!landing || aliens.length === 0) return;

      const geoJSON = {
        type: "FeatureCollection",
        features: [
          {
            type: "Feature",
            geometry: {
              type: "Point",
              coordinates: [landing.lng, landing.lat],
            },
            properties: { type: "landing" },
          },
          ...aliens.map((alien, idx) => ({
            type: "Feature",
            geometry: {
              type: "Point",
              coordinates: [
                alien.currentPos[1],
                alien.currentPos[0],
              ],
            },
            properties: { type: "alien", id: idx + 1 },
          })),
        ],
      };

      axios.post('http://localhost:5000/api/update-invasion', geoJSON);
    }, 1000);

    return () => clearInterval(interval);
  }, [landing, aliens]);

  return (
    <MapContainer center={center} zoom={10} style={{ height: '100vh' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <ClickHandler setLanding={setLanding} />
      {landing && <Marker position={landing} icon={landingIcon} />}
      {aliens.map((alien, idx) => (
        <React.Fragment key={idx}>
          <Polyline positions={alien.route} color="purple" dashArray="3" />
          <Marker
            position={alien.currentPos}
            icon={alienIcon(idx + 1)}
          />
        </React.Fragment>
      ))}
    </MapContainer>
  );
}
